mapper.insert:

	select replace(uuid(),'-','') from dual


insert into sys_menu


		id,


		parent_id,


		menu_name,


		menu_level,


		menu_type,


		menu_no,

		create_time,
		update_time,

		del_flag,






		#{id,jdbcType=VARCHAR}


		#{parentId,jdbcType=VARCHAR}


		#{menuName,jdbcType=VARCHAR}


		#{menuLevel,jdbcType=INTEGER}


		#{menuType,jdbcType=INTEGER}


		#{menuNo,jdbcType=INTEGER}

		now(),
		now(),

		#{delFlag,jdbcType=INTEGER}

mapper.update:
update sys_menu
parent_id = #{parentId,jdbcType=VARCHAR}, menu_name = #{menuName,jdbcType=VARCHAR}, menu_level = #{menuLevel,jdbcType=INTEGER}, menu_type = #{menuType,jdbcType=INTEGER}, menu_no = #{menuNo,jdbcType=INTEGER}, update_time=now(), del_flag = #{delFlag,jdbcType=INTEGER},
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select

from sys_menu
where 1=1

		and del_flag = #{del_flag}



		limit #{offset}, #{limit}
