mapper.insert:

	select replace(uuid(),'-','') from dual


insert into sys_role


		id,


		role_name,


		role_code,


		role_type,

		create_time,
		update_time,

		del_flag,






		#{id,jdbcType=VARCHAR}


		#{role_name,jdbcType=VARCHAR}


		#{role_code,jdbcType=VARCHAR}


		#{role_type,jdbcType=INTEGER}

		now(),
		now(),

		#{delFlag,jdbcType=INTEGER}

mapper.select:
select

from sys_role
where 1=1

		and del_flag = #{del_flag}


		and role_type = #{role_type}


		limit #{offset}, #{limit}
